mapper.select:
SELECT board.id, board.title, board.content, board.created, board.updated, user.user_id, user.name as user_name
FROM boards_board AS board JOIN users_user AS user ON board.user_id = user.user_id WHERE board.title like CONCAT('%',#{word},'%') OR board.content like CONCAT('%',#{word},'%')
ORDER BY board.created DESC LIMIT #{pagingvo.perPage} OFFSET #{pagingvo.limitStart}
